-- Create "media" table
CREATE TABLE "media" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "filename" character varying NOT NULL, "file_type" character varying NOT NULL, "storage_type" smallint NOT NULL, "path" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create "users" table
CREATE TABLE "users" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "status" smallint NOT NULL DEFAULT 1, "username" character varying NOT NULL, "password" character varying NOT NULL, "salt" character varying NOT NULL, "nickname" character varying NULL, "remark" character varying NULL, "phone" character varying NOT NULL, "email" character varying NOT NULL, "avatar_url" character varying NULL, PRIMARY KEY ("id"));
-- Create "trips" table
CREATE TABLE "trips" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "status" boolean NOT NULL DEFAULT true, "title" character varying NOT NULL, "description" character varying NOT NULL, "start_date" timestamptz NOT NULL, "end_date" timestamptz NOT NULL, "user_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "trips_users_trip" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "daily_trips" table
CREATE TABLE "daily_trips" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "day" integer NOT NULL, "date" timestamptz NOT NULL, "notes" character varying NULL, "trip_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "daily_trips_trips_daily_trip" FOREIGN KEY ("trip_id") REFERENCES "trips" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "cities" table
CREATE TABLE "cities" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create "continents" table
CREATE TABLE "continents" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create "countries" table
CREATE TABLE "countries" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create "states" table
CREATE TABLE "states" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create "points_of_interest" table
CREATE TABLE "points_of_interest" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, "description" character varying NOT NULL, "address" character varying NOT NULL, "latitude" double precision NOT NULL, "longitude" double precision NOT NULL, "type" character varying NOT NULL, "category" character varying NOT NULL, "rating" real NOT NULL, "recommended_duration_minutes" bigint NOT NULL, "city_id" uuid NULL, "continent_id" uuid NULL, "country_id" uuid NULL, "state_id" uuid NULL, PRIMARY KEY ("id"), CONSTRAINT "points_of_interest_cities_poi" FOREIGN KEY ("city_id") REFERENCES "cities" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "points_of_interest_continents_poi" FOREIGN KEY ("continent_id") REFERENCES "continents" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "points_of_interest_countries_poi" FOREIGN KEY ("country_id") REFERENCES "countries" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "points_of_interest_states_poi" FOREIGN KEY ("state_id") REFERENCES "states" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
-- Create "daily_itineraries" table
CREATE TABLE "daily_itineraries" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "notes" character varying NULL, "daily_trip_id" uuid NOT NULL, "poi_id" uuid NOT NULL, "trip_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "daily_itineraries_daily_trips_daily_itinerary" FOREIGN KEY ("daily_trip_id") REFERENCES "daily_trips" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "daily_itineraries_points_of_interest_daily_itinerary" FOREIGN KEY ("poi_id") REFERENCES "points_of_interest" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "daily_itineraries_trips_daily_itinerary" FOREIGN KEY ("trip_id") REFERENCES "trips" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "refresh_tokens" table
CREATE TABLE "refresh_tokens" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "token" character varying NOT NULL, "expires_at" timestamptz NOT NULL, "user_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "refresh_tokens_users_refresh_token" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
