-- Create "media" table
CREATE TABLE "media" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "filename" character varying NOT NULL, "file_type" character varying NOT NULL, "storage_type" smallint NOT NULL, "path" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create "continents" table
CREATE TABLE "continents" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, "code" character varying NOT NULL, PRIMARY KEY ("id"));
-- Create "countries" table
CREATE TABLE "countries" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, "code" character varying NOT NULL, "continent_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "countries_continents_country" FOREIGN KEY ("continent_id") REFERENCES "continents" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "states" table
CREATE TABLE "states" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, "code" character varying NOT NULL, "country_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "states_countries_state" FOREIGN KEY ("country_id") REFERENCES "countries" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "cities" table
CREATE TABLE "cities" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, "code" character varying NOT NULL, "state_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "cities_states_city" FOREIGN KEY ("state_id") REFERENCES "states" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "users" table
CREATE TABLE "users" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "status" smallint NOT NULL DEFAULT 1, "username" character varying NOT NULL, "password" character varying NOT NULL, "salt" character varying NOT NULL, "nickname" character varying NULL, "remark" character varying NULL, "phone" character varying NOT NULL, "email" character varying NOT NULL, "avatar_url" character varying NULL, PRIMARY KEY ("id"));
-- Create "trips" table
CREATE TABLE "trips" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "status" boolean NOT NULL DEFAULT true, "title" character varying NOT NULL, "description" character varying NOT NULL, "start_date" timestamptz NOT NULL, "end_date" timestamptz NOT NULL, "days" bigint NOT NULL, "user_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "trips_users_trip" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "daily_trips" table
CREATE TABLE "daily_trips" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "day" integer NOT NULL, "date" timestamptz NOT NULL, "notes" character varying NULL, "trip_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "daily_trips_trips_daily_trip" FOREIGN KEY ("trip_id") REFERENCES "trips" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "points_of_interest" table
CREATE TABLE "points_of_interest" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "name_en" character varying NOT NULL, "name_cn" character varying NOT NULL, "updated_by" uuid NOT NULL, "created_by" uuid NOT NULL, "description" character varying NOT NULL, "address" character varying NOT NULL, "latitude" double precision NOT NULL, "longitude" double precision NOT NULL, "type" character varying NOT NULL, "category" character varying NOT NULL, "rating" real NOT NULL, "recommended_duration_minutes" bigint NOT NULL, "city_id" bigint NULL, "continent_id" bigint NULL, "country_id" bigint NULL, "state_id" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "points_of_interest_cities_poi" FOREIGN KEY ("city_id") REFERENCES "cities" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "points_of_interest_continents_poi" FOREIGN KEY ("continent_id") REFERENCES "continents" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "points_of_interest_countries_poi" FOREIGN KEY ("country_id") REFERENCES "countries" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "points_of_interest_states_poi" FOREIGN KEY ("state_id") REFERENCES "states" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
-- Create "daily_itineraries" table
CREATE TABLE "daily_itineraries" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "notes" character varying NULL, "daily_trip_id" uuid NOT NULL, "poi_id" uuid NOT NULL, "trip_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "daily_itineraries_daily_trips_daily_itinerary" FOREIGN KEY ("daily_trip_id") REFERENCES "daily_trips" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "daily_itineraries_points_of_interest_daily_itinerary" FOREIGN KEY ("poi_id") REFERENCES "points_of_interest" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "daily_itineraries_trips_daily_itinerary" FOREIGN KEY ("trip_id") REFERENCES "trips" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "daily_trip_locations" table
CREATE TABLE "daily_trip_locations" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "location_id" uuid NOT NULL, "location_type" character varying NOT NULL, "sequence" smallint NOT NULL, "country_daily_trip_location" bigint NULL, "daily_trip_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "daily_trip_locations_countries_daily_trip_location" FOREIGN KEY ("country_daily_trip_location") REFERENCES "countries" ("id") ON UPDATE NO ACTION ON DELETE SET NULL, CONSTRAINT "daily_trip_locations_daily_trips_daily_trip_locations" FOREIGN KEY ("daily_trip_id") REFERENCES "daily_trips" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "refresh_tokens" table
CREATE TABLE "refresh_tokens" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "token" character varying NOT NULL, "expires_at" timestamptz NOT NULL, "user_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "refresh_tokens_users_refresh_token" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "trip_collaborators" table
CREATE TABLE "trip_collaborators" ("trip_id" uuid NOT NULL, "user_id" uuid NOT NULL, PRIMARY KEY ("trip_id", "user_id"), CONSTRAINT "trip_collaborators_trip_id" FOREIGN KEY ("trip_id") REFERENCES "trips" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "trip_collaborators_user_id" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
